import * as lorem from 'lorem-ipsum';

import { SimpleModule } from './simple.module';
import { SimpleViewComponent } from './simple-view.component';
import { sandboxOf } from 'angular-playground';

const text = lorem({count: 15, units: 'sentences'});

export default sandboxOf(SimpleViewComponent, {
    imports: [SimpleModule ],
    declareComponent: false
  })
  .add('with basic use-case', {
    template: `<a-simple-view
                 title="simple works!">
               </a-simple-view>`
  })
  .add('with large strings', {
    template: `<a-simple-view
                 title="${text}">
               </a-simple-view>`
  });
